package ConditionalStatements;

public class ConditionalStatements4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a =15;
		int b=7;
		int c=30;
		
		if (a>b)
		{
			if(a>c)
			{
				System.out.println("a is greatest");
			}
			else
			{
				System.out.println("c is greatest");
				
			}
			
		}
	}

}
